const AccountRequestPopup = ({ handleClose }) => {
  const [formData, setFormData] = useState({
    companyName: '',
    Prénom: '',
    email: '',
    phone: '',
    Matricule: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Soumettre le formulaire ou effectuer d'autres actions avec les données
    console.log(formData);
    // Réinitialiser le formulaire après la soumission si nécessaire
    setFormData({
      Nom: '',
      Prénom: '',
      email: '',
      phone: '',
      message: '',
    });
    // Fermer la popup
    handleClose();
  };

  const handleCancel = () => {
    // Gérer l'annulation, par exemple, fermer la popup
    handleClose();
  };

  return (
    <PopupOverlay>
      <PopupContent>
        <CloseButton onClick={handleClose}>&times;</CloseButton>
        <Title>Demande de compte</Title>
        <Form onSubmit={handleSubmit}>
          <Label htmlFor="Nom">Nom de l'employé</Label>
          <Input
            type="text"
            id="Nom"
            name="Nom"
            value={formData.Nom}
            onChange={handleChange}
            required
          />
          <Label htmlFor="Prénom">Prénom de l'employé</Label>
          <Input
            type="text"
            id="Prénom"
            name="Prénom"
            value={formData.Prénom}
            onChange={handleChange}
            required
          />
          <Label htmlFor="email">Email</Label>
          <Input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
          <Label htmlFor="phone">Téléphone</Label>
          <Input
            type="tel"
            id="phone"
            name="phone"
            value={formData.phone}
            onChange={handleChange}
            required
          />
          <Label htmlFor="Matricule">Matricule </Label>
          <Input
            type="text"
            id="Matricule"
            name="Matricule"
            value={formData.Matricule}
            onChange={handleChange}
          />
          <div>
            <Button type="submit">Soumettre la demande</Button>
            <CancelButton type="button" onClick={handleCancel}>Annuler</CancelButton>
          </div>
        </Form>
      </PopupContent>
    </PopupOverlay>
  );
};
