import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import { useEffect, useRef, useState } from "react";
import styled from "styled-components";
import logo from "../../assets/logo2.png";
import LoginPopup from "../../Sections/LoginPopup";
import "./hed.css";
import AccountRequestPopup from "../../Sections/AccountRequestPopup";

const Headers = styled.header`
  display: flex;
  height: 85px;
  justify-content: space-between;
  align-items: center;
  padding: 1rem 5rem;
  background-color: #799aa23d;
  color: var(--black);
  position: relative;
  z-index: 500;
  backdrop-filter: blur(30px);
  -webkit-backdrop-filter: blur(30px);
  @media only Screen and (max-width: 64em) {
    padding: 0.5rem 3rem;
  }
  @media only Screen and (max-width: 40em) {
    padding: 0.5rem 1.5rem;
  }
`;

const Logo = styled.a`
  display: flex;
  align-items: center;
  width: 2rem;
  height: auto;
  cursor: pointer;
  img {
    margin-right: 0.5rem;
  }
`;

const Nav = styled.nav`
  width: 25rem;
  display: flex;
  align-items: center;
  justify-content: space-between;
  transition: all 0.3s;

  @media only Screen and (max-width: 48em) {
    display: none;
  }
  a {
    font-weight: 600;
    line-height: 1.5;
    color: var(--black);
    &::after {
      content: "";
      display: block;
      height: 3px;
      width: 0;
      background: transparent;
      transition: width 0.5s;
    }
    &:not(:last-child):hover::after {
      width: 100%;
      background: var(--purple);
    }
  }
`;

const Button = styled.button`
  background-color: var(--purple);
  padding: 0.5rem 1rem;
  border-radius: 20px;
  color: var(--black);
  font-weight: 600;
  cursor: pointer;
  transition: all 0.2s;
  &:hover {
    transform: scale(1.1);
  }
  &:focus {
    transform: scale(0.9);
  }
  @media only Screen and (max-width: 40em) {
    font-size: 1.2rem;
    &:hover {
      transform: none;
    }
    &:focus {
      transform: none;
    }
  }
`;

const PopupContainer = styled.div`
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  z-index: 1000;
`;

const Header = () => {
  const [click, setClick] = useState(false);
  const [showLoginPopup, setShowLoginPopup] = useState(false);
  const [showAccountRequestPopup, setShowAccountRequestPopup] = useState(false);

  const ref = useRef(null);
  gsap.registerPlugin(ScrollTrigger);

  const scrollUp = (id, e) => {
    e.preventDefault();
    const element = document.getElementById(id);
    if (element) {
      element.scrollIntoView({
        behavior: "smooth",
        block: "end",
        inline: "nearest",
      });
    }
  };

  const handleClick = (id, e) => {
    e.preventDefault();
    setClick(!click);
    if (id === "se connecter") {
      console.log("Bouton 'Se connecter' cliqué");
      setShowLoginPopup(true);
    } else if (id === "Demander un compte") {
      console.log("Bouton 'Demander un compte' cliqué");
      setShowAccountRequestPopup(true);
    } else {
      scrollUp(id, e);
    }
  };

  const handleClosePopup = () => {
    setShowLoginPopup(false);
    setShowAccountRequestPopup(false);
  };

  useEffect(() => {
    const element = ref.current;

    const mq = window.matchMedia("(max-width: 40em)");
    if (mq.matches) {
      gsap.to(element, {
        position: "fixed",
        top: "0",
        left: "0",
        right: "0",
        padding: "1rem 2.5rem",
        borderRadius: "0 0 50px 50px",
        border: "2px solid var(--white)",
        duration: 1,
        ease: "power1.out",
        scrollTrigger: {
          trigger: element,
          start: "bottom+=200 top",
          end: "+=100",
          scrub: true,
        },
      });
    } else {
      gsap.to(element, {
        position: "fixed",
        top: "1rem",
        left: "3rem",
        right: "3rem",
        padding: "1.5rem 2rem",
        borderRadius: "50px",
        border: "3px solid var(--white)",
        duration: 1,
        ease: "power1.out",
        scrollTrigger: {
          trigger: element,
          start: "bottom+=300 top",
          end: "+=250",
          scrub: true,
        },
      });
    }
  }, []);

  return (
    <Headers ref={ref}>
      <Logo>
        <img src={logo} alt="logo" />
        <div className="text-container">
          <span className="text-item">Gestion</span>
          <span className="text-item">Des Matériaux</span>
        </div>
      </Logo>
      <Nav>
        <div className="a1">
          <a className="home" href="#home" onClick={(e) => scrollUp("home", e)}>
            Accueil
          </a>
        </div>
        <a className="about" href="#about" onClick={(e) => scrollUp("about", e)}>
          À-propos
        </a>
        <a className="services" href="#services" onClick={(e) => scrollUp("services", e)}>
          Services
        </a>
        <a className="contact" href="#contact" onClick={(e) => handleClick("contact", e)}>
          Contact
        </a>
        <a className="se connecter" href="#se connecter" onClick={(e) => handleClick("se connecter", e)}>
          <Button>Se connecter</Button>
        </a>
        <a className="Demander un compte " href="#Demander un compte " onClick={(e) => handleClick("Demander un compte", e)}>
          <Button>Demander de compte</Button>
        </a>
      </Nav>
      {showLoginPopup && (
        <PopupContainer>
          <LoginPopup handleClose={() => setShowLoginPopup(false)} />
        </PopupContainer>
      )}
      {showAccountRequestPopup && (
        <PopupContainer>
          <AccountRequestPopup handleClose={handleClosePopup} />
        </PopupContainer>
      )}

      {/* Reste du code pour le bouton hamburger et le menu mobile */}
    </Headers>
  );
};

export default Header;
